import React,{useEffect,Fragment} from 'react'
import PropTypes from 'prop-types';
import {connect} from 'react-redux';
import Spinner from '../layout/Spinner';
import {getProfileById} from '../../actions/profile';
import ProfileTop from './ProfileTop';
import ProfileAbout from './ProfileAbout';
import { Link } from 'react-router-dom';
import ProfileExperience from './ProfileExperience';
import ProfileEducation from './ProfileEducation';
import ProfileGithub from './ProfileGitHub';


const Profile = ({match,auth,getProfileById,profile:{
    profile,
    loading
}}) => {

useEffect(() => {
 getProfileById(match.params.id);   
}, [getProfileById,match.params.id])

    return (
        <Fragment>
            {profile === null || loading ? <Spinner/> : 
            <Fragment>
            <Link to='/profiles' className='btn btn-light'>
            Back to Profiles
            </Link>
            {/* HERE WE CHECK IF THE ACTUAL USER VIEWING THE PROFILE IS THE OWNER ONLY THAN CAN EDIT PROFILE */}
            {auth.isAuth && auth.loading === false && auth.user._id === profile.user._id &&
            (<Link to='/edit-profile' className='btn btn-dark'>
            Edit profile
            </Link>
            )}
            <div className='profile-grid my-1'>
                <ProfileTop profile={profile}/>
                <ProfileAbout profile={profile}/>

                <div className="profile-exp bg-white p-2">
                    <h2 className="text-primary">Experience</h2>
                    {profile.experience.length > 0 ? 
                    (
                        <Fragment>
                     {profile.experience.map(exp => (
                         <ProfileExperience key={exp._id} experience={exp}/>
                     ))}
                        </Fragment>
                    ) 
                        : (<h4>No Experience Credentials</h4>)}
                </div>
                <div className="profile-edu bg-white p-2">
                    <h2 className="text-primary">Education</h2>
                    {profile.education.length > 0 ? 
                    (
                        <Fragment>
                     {profile.education.map(edu => (
                         <ProfileEducation key={edu._id} experience={edu}/>
                     ))}
                        </Fragment>
                    ) 
                        : (<h4>No Education Credentials</h4>)}
                </div>
                {profile.githubusername &&(
                    <ProfileGithub username={profile.githubusername} />
                )}
            </div>
            </Fragment> 
                }
        </Fragment>
    )
}

Profile.propTypes = {
getProfileById: PropTypes.func.isRequired,
profile: PropTypes.object.isRequired,
auth: PropTypes.object.isRequired,
}

const mapStateToProps = state =>({
profile: state.profile,
auth: state.auth
})
export default connect(mapStateToProps,{getProfileById})(Profile);
